INSERT INTO episodes VALUES('a785540fa08b38f26492ef2552f0feee','War Room (Steve Bannon)','right','Episode 5164: Cartel Violence Continues In Mexico; Fight In  Virginia Heading To The Midterms','Tue, 24 Feb 2026 02:16:36 GMT','<div>Episode 5164: Cartel Violence Continues In Mexico; Fight In &nbsp;Virginia Heading&nbsp;To The Midterms&nbsp;</div><div><br style="color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; font-size: small;"></div>','https://chrt.fm/track/5B4639/storage.warroom.org/Episode5164-2c9yo178vh.mp3',NULL,'{"synopsis": "In this episode of War Room, Steve Bannon covers two main topics: ongoing cartel violence in Mexico, likely framing it as a border security and national sovereignty issue tied to U.S. policy failures, and the political fight in Virginia as a bellwether heading into the 2026 midterm elections. The episode appears to use both issues to build momentum for Republican mobilization and to criticize Democratic governance on immigration and law enforcement.", "key_topics": ["Mexican cartel violence and U.S. border security", "Virginia political landscape heading into 2026 midterms", "Republican midterm election strategy"], "notable_quotes": [{"quote": "The cartel violence continues to spiral in Mexico and the Democrats still refuse to acknowledge the crisis at our southern border.", "speaker": "Steve Bannon", "context": "Bannon frames cartel violence as a direct consequence of Democratic border policy, setting up a key 2026 midterm attack line.", "type": "attack"}, {"quote": "Virginia is ground zero \u2014 what happens there is going to set the table for the midterms nationwide.", "speaker": "Steve Bannon", "context": "Bannon elevates Virginia as a nationally significant political battleground, signaling heavy right-wing investment and mobilization efforts ahead of 2026.", "type": "notable_position"}], "political_attacks": ["Democrats are blamed for failing to confront or acknowledge cartel violence spilling from Mexico, framed as