INSERT INTO episodes VALUES('479976b59e600f56b750d5987f6fa489','War Room (Steve Bannon)','right','Episode 5163: Mexico Falls Into Chaos After Execution Of Cartel Kingpin','Mon, 23 Feb 2026 20:56:40 GMT','<div style="color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; font-size: small;"><span style="color: rgb(75, 85, 99); font-family: ui-sans-serif, system-ui, -apple-system, &quot;Segoe UI&quot;, Roboto, Ubuntu, Cantarell, &quot;Noto Sans&quot;, sans-serif, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, Arial, &quot;Apple Color Emoji&quot;, &quot;Segoe UI Emoji&quot;, &quot;Segoe UI Symbol&quot;, &quot;Noto Color Emoji&quot;; font-size: 14px; background-color: rgb(243, 244, 246);">Episode 5163: Mexico Falls Into Chaos After Execution Of Cartel Kingpin&nbsp;</span></div><div><span style="color: rgb(75, 85, 99); font-family: ui-sans-serif, system-ui, -apple-system, &quot;Segoe UI&quot;, Roboto, Ubuntu, Cantarell, &quot;Noto Sans&quot;, sans-serif, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, Arial, &quot;Apple Color Emoji&quot;, &quot;Segoe UI Emoji&quot;, &quot;Segoe UI Symbol&quot;, &quot;Noto Color Emoji&quot;; font-size: 14px; background-color: rgb(243, 244, 246);"><br></span></div>','https://chrt.fm/track/5B4639/storage.warroom.org/Episdoe5163-n4c68x3u2a.mp3',NULL,'{"synopsis": "In this episode of War Room, Steve Bannon covers the reported execution of a cartel kingpin in Mexico and the ensuing chaos, using the event to frame Mexico as a failed state and to bolster arguments for aggressive U.S. border and national security policies. The episode likely ties the violence to what Bannon frames as failed Biden-era border policies and argues for hardline Trump administration interventions including potential military or quasi-military action against cartels.", "key_topics": ["Mexican cartel violence and instability", "U.S.-Mexico border security", "Cartel designation as terrorist organizations", "U.S. military or intervention options in Mexico"], "n