PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE episodes (
            id TEXT PRIMARY KEY,
            podcast_name TEXT,
            lean TEXT,
            title TEXT,
            published TEXT,
            description TEXT,
            audio_url TEXT,
            transcript TEXT,
            analysis TEXT,
            fetched_at TEXT,
            digest_included INTEGER DEFAULT 0
        );
CREATE TABLE digests (
            id TEXT PRIMARY KEY,
            date TEXT,
            content_html TEXT,
            content_text TEXT,
            created_at TEXT
        );
COMMIT;
